const {
  reset
} = require('../../dom.js');
const {
  getBaseLayerHoverDelegate
} = require('../hover/hoverDelegate2.js');
const {
  getDefaultHoverDelegate
} = require('../hover/hoverDelegateFactory.js');
const {
  renderLabelWithIcons
} = require('./iconLabels.js');
class SimpleIconLabel {
  constructor(_container) {
    this._container = _container;
  }
  set text(text) {
    reset(this._container, ...renderLabelWithIcons(text ?? ''));
  }
  set title(title) {
    if (!this.hover && title) {
      this.hover = getBaseLayerHoverDelegate().setupUpdatableHover(getDefaultHoverDelegate('mouse'), this._container, title);
    } else if (this.hover) {
      this.hover.update(title);
    }
  }
  dispose() {
    this.hover?.dispose();
  }
}
exports.SimpleIconLabel = SimpleIconLabel;