const {
  createDecorator
} = require('../../../platform/instantiation/common/instantiation.js');
const ITextModelService = createDecorator('textModelService');
function isResolvedTextEditorModel(model) {
  const candidate = model;
  return !!candidate.textEditorModel;
}
exports.ITextModelService = ITextModelService;
exports.isResolvedTextEditorModel = isResolvedTextEditorModel;