const {
  registerEditorContribution,
  registerEditorAction
} = require('../../../browser/editorExtensions.js');
const {
  HoverParticipantRegistry
} = require('../../hover/browser/hoverTypes.js');
const {
  TriggerInlineSuggestionAction,
  ShowNextInlineSuggestionAction,
  ShowPreviousInlineSuggestionAction,
  AcceptNextWordOfInlineCompletion,
  AcceptNextLineOfInlineCompletion,
  AcceptInlineCompletion,
  HideInlineCompletion,
  ToggleAlwaysShowInlineSuggestionToolbar
} = require('./commands.js');
const {
  InlineCompletionsHoverParticipant
} = require('./hoverParticipant.js');
const {
  InlineCompletionsController
} = require('./inlineCompletionsController.js');
const {
  registerAction2
} = require('../../../../platform/actions/common/actions.js');
registerEditorContribution(InlineCompletionsController.ID, InlineCompletionsController, 3);
