const {
  registerEditorAction,
  registerEditorContribution
} = require('../../../browser/editorExtensions.js');
const {
  HoverParticipantRegistry
} = require('../../hover/browser/hoverTypes.js');
const {
  AcceptInlineEdit,
  RejectInlineEdit,
  JumpToInlineEdit,
  JumpBackInlineEdit,
  TriggerInlineEdit
} = require('./commands.js');
const {
  InlineEditHoverParticipant
} = require('./hoverParticipant.js');
const {
  InlineEditController
} = require('./inlineEditController.js');
registerEditorAction(AcceptInlineEdit);
registerEditorAction(RejectInlineEdit);
registerEditorAction(JumpToInlineEdit);
registerEditorAction(JumpBackInlineEdit);
registerEditorAction(TriggerInlineEdit);
registerEditorContribution(InlineEditController.ID, InlineEditController, 3);
HoverParticipantRegistry.register(InlineEditHoverParticipant);