const {
  localize2WithPath
} = require('../../../nls.js');
const {
  Categories
} = require('../../action/common/actionCommonCategories.js');
const {
  Action2
} = require('./actions.js');
const {
  IMenuService
} = require('./actions.service.js');
const {
  ILogService
} = require('../../log/common/log.service.js');
const _moduleId = "vs/platform/actions/common/menuResetAction";
class MenuHiddenStatesReset extends Action2 {
  constructor() {
    super({
      id: 'menu.resetHiddenStates',
      title: localize2WithPath(_moduleId, 0, "Reset All Menus"),
      category: Categories.View,
      f1: true
    });
  }
  run(accessor) {
    accessor.get(IMenuService).resetHiddenStates();
    accessor.get(ILogService).info('did RESET all menu hidden states');
  }
}
exports.MenuHiddenStatesReset = MenuHiddenStatesReset;