const {
  isEditorGroup
} = require('./editorGroupsService.js');
const ACTIVE_GROUP = -1;
const SIDE_GROUP = -2;
const AUX_WINDOW_GROUP = -3;
function isPreferredGroup(obj) {
  const candidate = obj;
  return typeof obj === 'number' || isEditorGroup(candidate);
}
exports.ACTIVE_GROUP = ACTIVE_GROUP;
exports.AUX_WINDOW_GROUP = AUX_WINDOW_GROUP;
exports.SIDE_GROUP = SIDE_GROUP;
exports.isPreferredGroup = isPreferredGroup;